    public static ArrayList<Onepiece> getListData() {
        Onepiece onepiece = null;
        ArrayList<Onepiece> list = new ArrayList<>();
        for (String[] aData : data) {
            onepiece = new Onepiece();
            onepiece.setName(aData[0]);
            onepiece.setNickname(aData[1]);
            onepiece.setBounty(aData[2]);
            onepiece.setPhoto(aData[3]);
            onepiece.setDesc(aData[4]);

            list.add(onepiece);
        }
        return list;
    }
